#!/usr/bin/php<?php$min = 24.0;$max = 26.0;exec('gpio mode 0 out'); exec('gpio mode 2 out'); exec('gpio mode 3 out'); exec('gpio write 0 0'); exec('gpio write 2 0');exec('gpio write 3 0');for ($i=0;$i<5000;$i++) {  $temp01 = exec('cat /sys/bus/w1/devices/28-021562a3a0ff/w1_slave |grep t=');  $temp01 = explode('t=',$temp01);  $temp01 = $temp01[1] / 1000;  if ($temp01 < $min ) {    exec('gpio write 0 1');     exec('gpio write 2 0');    exec('gpio write 3 0');  } elseif ($temp01 > $max ) {    exec('gpio write 0 0');     exec('gpio write 2 0');    exec('gpio write 3 1');  } else {    exec('gpio write 0 0');     exec('gpio write 2 1');    exec('gpio write 3 0');  }  $temp01 = number_format($temp01,1,",",".")."Â°C";  echo date("H:i:s").". Temperatur ".$temp01."\n";}?>